/*
 * Example showing the code to retrieve snippets from GitHub
 * at the collection level.  Stored in the getCollectionLevelTests
 * environment variable
*/

//Setup the URL components
var owner = pm.collectionVariables.get("owner");
var repoSlug = pm.collectionVariables.get("repoSlug");
var path = pm.collectionVariables.get("collectionPath");
var token = pm.collectionVariables.get("personalAccessToken");
var baseUrl = pm.collectionVariables.get("baseUrl");
//Build the URL
var url = baseUrl + "/repos/" + repoSlug + "/contents/" + path;
console.log("URL: " + url);
const postRequest = {
  url: url,
  method: 'get',
  header: {
    'Content-Type': 'application/json',
    'Authorization': 'bearer ' + token
  }
};
pm.sendRequest(postRequest, (err, res) => {
    var jsonData = JSON.parse(res.text());
var base64content = jsonData.content;
var buff = new Buffer(base64content, 'base64');
var content = buff.toString('ascii');
pm.collectionVariables.set("collectionTestLibrary", content);

});

